import { Search } from 'lucide-react';
import React, { useState } from 'react';

import type { FAQItemWithIndex } from '../types';
import FAQItem from './FAQItem';

interface FAQTabbedInterfaceProps {
  groupedFAQs: Record<string, FAQItemWithIndex[]>;
  categories: string[];
  openItems: number[];
  onToggleItem: (index: number) => void;
}

const FAQTabbedInterface: React.FC<FAQTabbedInterfaceProps> = ({
  groupedFAQs,
  categories,
  openItems,
  onToggleItem
}) => {
  const [activeCategory, setActiveCategory] = useState(categories[0] || '');
  const [searchTerm, setSearchTerm] = useState('');

  // Filter FAQs based on search term across all categories
  const getFilteredFAQs = () => {
    if (!searchTerm.trim()) {
      return groupedFAQs[activeCategory] || [];
    }

    // Search across all categories when there's a search term
    const allFAQs = Object.values(groupedFAQs).flat();
    return allFAQs.filter(item =>
      item.question.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.answer.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  const filteredFAQs = getFilteredFAQs();
  const isSearching = searchTerm.trim().length > 0;

  return (
    <div className="bg-stone-800 rounded-xl p-6 shadow-xl">
      {/* Search Bar */}
      <div className="mb-8">
        <div className="relative max-w-md mx-auto">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
          <input
            type="text"
            id="faq-search"
            name="faqSearch"
            placeholder="Search FAQs..."
            value={searchTerm}
            onChange={(e) => { setSearchTerm(e.target.value); }}
            className="w-full pl-10 pr-4 py-3 bg-stone-700 border border-stone-600 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all"
          />
        </div>
      </div>

      {!isSearching && (
        <>
          {/* Category Tabs - Horizontal scrollable on mobile */}
          <div className="mb-8">
            <div className="border-b border-stone-600">
              <div className="flex overflow-x-auto scrollbar-thin scrollbar-thumb-stone-600 scrollbar-track-transparent">
                <div className="flex space-x-1 min-w-max px-1">
                  {categories.map((category) => (
                    <button
                      key={category}
                      onClick={() => { setActiveCategory(category); }}
                      className={`
                        px-6 py-3 font-semibold text-sm whitespace-nowrap rounded-t-lg transition-all duration-200
                        ${activeCategory === category
                          ? 'text-orange-400 bg-stone-700 border-b-2 border-orange-400'
                          : 'text-gray-300 hover:text-white hover:bg-stone-700/50'
                        }
                      `}
                      aria-selected={activeCategory === category}
                      role="tab"
                    >
                      {category}
                      <span className="ml-2 text-xs opacity-75">
                        ({groupedFAQs[category]?.length || 0})
                      </span>
                    </button>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Active Category Content */}
          <div className="min-h-[400px]">
            <h3 className="text-xl font-bold text-orange-400 mb-6 text-center">
              {activeCategory}
            </h3>
            
            {/* FAQ Items in Grid Layout */}
            <div className="grid gap-4 lg:grid-cols-2 xl:grid-cols-2">
              {(groupedFAQs[activeCategory] || []).map((item) => (
                <div key={item.originalIndex} className={openItems.includes(item.originalIndex) ? "h-fit" : "h-[120px]"}>
                  <FAQItem
                    item={item}
                    isOpen={openItems.includes(item.originalIndex)}
                    onToggle={() => { onToggleItem(item.originalIndex); }}
                  />
                </div>
              ))}
            </div>

            {(!groupedFAQs[activeCategory] || groupedFAQs[activeCategory].length === 0) && (
              <div className="text-center py-12 text-gray-400">
                <p>No FAQs found in this category.</p>
              </div>
            )}
          </div>
        </>
      )}

      {/* Search Results */}
      {isSearching && (
        <div className="min-h-[400px]">
          <h3 className="text-xl font-bold text-orange-400 mb-6 text-center">
            Search Results ({filteredFAQs.length})
          </h3>
          
          <div className="grid gap-4 lg:grid-cols-2 xl:grid-cols-2">
            {filteredFAQs.map((item) => (
              <div key={item.originalIndex} className={openItems.includes(item.originalIndex) ? "h-fit" : "h-[120px]"}>
                <FAQItem
                  item={item}
                  isOpen={openItems.includes(item.originalIndex)}
                  onToggle={() => { onToggleItem(item.originalIndex); }}
                  highlightTerm={searchTerm}
                />
              </div>
            ))}
          </div>

          {filteredFAQs.length === 0 && (
            <div className="text-center py-12 text-gray-400">
              <p>No FAQs found matching &ldquo;{searchTerm}&rdquo;</p>
              <p className="text-sm mt-2">Try different keywords or browse by category above.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default FAQTabbedInterface;

import PropTypes from 'prop-types';
import React from 'react';

import { useFAQ } from '@/hooks/useFAQ';

import AffiliateFooter from '../components/AffiliateFooter';
import AffiliateHeader from '../components/AffiliateHeader';
import FAQExpandButton from '../components/FAQExpandButton';
import FAQTabbedInterface from '../components/FAQTabbedInterface';
import { useAffiliateData } from '../hooks/useAffiliateData';
import { useFAQEffects } from '../hooks/useFAQEffects';
import { useFAQState } from '../hooks/useFAQState';
import type { FAQProps, FAQRef } from '../types';

const FAQAffiliate = React.forwardRef<FAQRef, FAQProps>(
  ({ autoExpand = false }, ref) => {
    // Use global FAQ state for expansion
    const faqContext = useFAQ();
    const isExpanded = faqContext?.isExpanded ?? false;
    const setIsExpanded = React.useMemo(() => faqContext?.setIsExpanded ?? (() => {}), [faqContext?.setIsExpanded]);

    // Use local state for individual FAQ items
    const faqState = useFAQState(autoExpand);
    const openItems = faqState?.openItems ?? new Set<string>();
    const toggleItem = faqState?.toggleItem ?? (() => {});
    const resetState = faqState?.resetState ?? (() => {});

    // Custom hook for affiliate FAQ data
    const affiliateData = useAffiliateData();
    const faqData = affiliateData?.faqData ?? [];
    const groupedFAQs = affiliateData?.groupedFAQs ?? {};
    const categories = affiliateData?.categories ?? [];
    const geoConfig = affiliateData?.geoConfig;

    // Custom hook for side effects
    useFAQEffects({
      faqData,
      isExpanded,
      resetState,
      setIsExpanded
    });

    // Imperative handle for ref
    React.useImperativeHandle(ref, () => ({
      expand: () => { setIsExpanded(true); },
    }), [setIsExpanded]);

    return (
      <section className="bg-stone-900 py-16" id="faq" aria-labelledby="faq-heading">
        <div className="max-w-7xl mx-auto px-4">
          {!isExpanded ? (
            <FAQExpandButton onToggleExpanded={() => { setIsExpanded(true); }} />
          ) : (
            <div className="space-y-8">
              <AffiliateHeader
                geoConfig={geoConfig}
                onToggleExpanded={() => { setIsExpanded(false); }}
              />

              <FAQTabbedInterface
                groupedFAQs={groupedFAQs}
                categories={categories}
                openItems={openItems}
                onToggleItem={toggleItem}
              />

              <AffiliateFooter />
            </div>
          )}
        </div>
      </section>
    );
  }
);

FAQAffiliate.displayName = 'FAQAffiliate';

FAQAffiliate.propTypes = {
  autoExpand: PropTypes.bool,
};

export default FAQAffiliate;

