import React, { useState } from 'react';

import { useReviewVote } from './hooks/useReviews';
import { ReviewSourceIcon } from './ReviewSourceIcon';
import { StarRating } from './StarRating';
import type { ReviewCardProps } from './types';

export const ReviewCard: React.FC<ReviewCardProps> = ({ 
  review, 
  showVoting = false, 
  onVote,
  onReviewClick
}) => {
  const [hasVoted, setHasVoted] = useState(false);
  const [userVote, setUserVote] = useState<'helpful' | 'not_helpful' | null>(null);
  const [imageError, setImageError] = useState(false);
  const { voteOnReview, loading: votingLoading } = useReviewVote();

  // Text truncation settings
  const MAX_WORDS = 25;
  const words = review.reviewText.split(' ');
  const isTruncated = words.length > MAX_WORDS;
  const truncatedText = isTruncated ? words.slice(0, MAX_WORDS).join(' ') + '...' : review.reviewText;

  // Check if review is from this week (within last 7 days)
  const isThisWeek = () => {
    const reviewDate = new Date(review.date);
    const now = new Date();
    const diffTime = Math.abs(now.getTime() - reviewDate.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays <= 7;
  };

  const handleVote = async (voteType: 'helpful' | 'not_helpful') => {
    if (hasVoted || votingLoading) return;

    try {
      await voteOnReview(review.id, voteType);
      setHasVoted(true);
      setUserVote(voteType);
      onVote?.(review.id, voteType);
    } catch (error) {
      console.error('Failed to vote on review:', error);
    }
  };
  return (
    <div 
      className={`bg-stone-800 rounded-xl p-6 hover:bg-stone-700 transition-all duration-300 hover:scale-105 hover:shadow-2xl border hover:border-orange-400/30 cursor-pointer ${
        review.isFeatured ? 'border-orange-400/50 bg-gradient-to-br from-stone-800 to-stone-700' : 'border-stone-600'
      }`}
      onClick={(e) => {
        e.stopPropagation();
        onReviewClick?.(review);
      }}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          e.stopPropagation();
          onReviewClick?.(review);
        }
      }}
      role="button"
      tabIndex={0}
    >
      {/* This Week badge */}
      {isThisWeek() && (
        <div className="absolute -top-2 -right-2 bg-orange-400 text-stone-900 text-xs font-bold px-2 py-1 rounded-full">
          This Week
        </div>
      )}

      {/* Header with profile and rating */}
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-center gap-3">
          {/* Profile image */}
          <div className="w-12 h-12 rounded-full bg-gradient-to-br from-orange-400 to-orange-500 flex items-center justify-center text-white font-bold text-3xl overflow-hidden">
            {review.profileImage && !imageError ? (
              <img 
                src={review.profileImage} 
                alt={review.customerName}
                className="w-full h-full object-cover"
                onError={() => { setImageError(true); }}
              />
            ) : (
              review.customerName.charAt(0).toUpperCase()
            )}
          </div>
          <div>
            <h3 className="text-white font-semibold text-lg leading-tight">
              {review.customerName}
            </h3>
            <div className="flex items-center gap-2 mt-1">
              <StarRating rating={review.rating} size="sm" />
              {review.isVerified && (
                <span className="text-orange-400 text-xs font-medium">
                  Verified
                </span>
              )}
              {review.reviewSource && (
                <ReviewSourceIcon source={review.reviewSource} size="sm" />
              )}
              {review.serviceCategory && (
                <span className="text-stone-400 text-xs bg-stone-700 px-2 py-1 rounded">
                  {review.serviceCategory}
                </span>
              )}
            </div>
          </div>
        </div>

      </div>

      {/* Review title */}
      {review.title && (
        <h4 className="text-white font-semibold text-base mb-2">
          {review.title}
        </h4>
      )}

      {/* Review text */}
      <p className="text-gray-300 leading-relaxed mb-4">
        &quot;{truncatedText}&quot;
      </p>

      {/* Voting section */}
      {showVoting && (review.helpfulVotes !== undefined || review.totalVotes !== undefined) && (
        <div 
          className="mb-4 p-3 bg-stone-700/50 rounded-lg"
          onClick={(e) => { e.stopPropagation(); }}
          onKeyDown={(e) => {
            if (e.key === 'Enter' || e.key === ' ') {
              e.preventDefault();
              e.stopPropagation();
            }
          }}
          role="button"
          tabIndex={0}
        >
          <div className="flex items-center justify-between mb-2">
            <span className="text-stone-300 text-sm">Was this review helpful?</span>
            <span className="text-stone-400 text-xs">
              {review.helpfulVotes || 0} of {review.totalVotes || 0} found helpful
            </span>
          </div>
          <div className="flex gap-2">
            <button
              onClick={(e) => {
                e.stopPropagation();
                void handleVote('helpful');
              }}
              disabled={hasVoted || votingLoading}
              className={`px-3 py-1 rounded text-xs font-medium transition-colors ${
                hasVoted && userVote === 'helpful'
                  ? 'bg-green-600 text-white'
                  : hasVoted
                  ? 'bg-stone-600 text-stone-400 cursor-not-allowed'
                  : 'bg-stone-600 text-stone-300 hover:bg-green-600 hover:text-white'
              }`}
            >
              {votingLoading ? '...' : 'üëç Helpful'}
            </button>
            <button
              onClick={(e) => {
                e.stopPropagation();
                void handleVote('not_helpful');
              }}
              disabled={hasVoted || votingLoading}
              className={`px-3 py-1 rounded text-xs font-medium transition-colors ${
                hasVoted && userVote === 'not_helpful'
                  ? 'bg-red-600 text-white'
                  : hasVoted
                  ? 'bg-stone-600 text-stone-400 cursor-not-allowed'
                  : 'bg-stone-600 text-stone-300 hover:bg-red-600 hover:text-white'
              }`}
            >
              {votingLoading ? '...' : 'üëé Not helpful'}
            </button>
          </div>
        </div>
      )}

      {/* Date */}
      <div className="flex items-center justify-between pt-3 border-t border-stone-600">
        <span className="text-gray-400 text-sm">
          {new Date(review.date).toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'long',
            day: 'numeric'
          })}
        </span>
        <div className="flex gap-1">
          <div className="w-2 h-2 bg-orange-400 rounded-full animate-pulse"></div>
          <div className="w-2 h-2 bg-orange-400/60 rounded-full animate-pulse delay-150"></div>
          <div className="w-2 h-2 bg-orange-400/30 rounded-full animate-pulse delay-300"></div>
        </div>
      </div>

    </div>
  );
};

import React, { useState } from 'react';

import { useSubmitReview } from './hooks/useReviews';
import { StarRating } from './StarRating';
import type { ReviewFormData, ReviewFormProps } from './types';

export const ReviewForm: React.FC<ReviewFormProps> = ({
  businessSlug,
  businessName,
  reviewType,
  onSubmit,
  onCancel,
  isLoading = false
}) => {
  const [formData, setFormData] = useState<ReviewFormData>({
    rating: 5,
    title: '',
    content: '',
    reviewer_name: '',
    reviewer_email: '',
    reviewer_phone: '',
    service_category: '',
    service_date: ''
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const { submitReview, loading: submitLoading } = useSubmitReview();

  const handleInputChange = (field: keyof ReviewFormData, value: string | number) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.reviewer_name.trim()) {
      newErrors.reviewer_name = 'Name is required';
    }

    if (!formData.content.trim()) {
      newErrors.content = 'Review content is required';
    } else if (formData.content.trim().length < 10) {
      newErrors.content = 'Review must be at least 10 characters long';
    }

    if (formData.reviewer_email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.reviewer_email)) {
      newErrors.reviewer_email = 'Please enter a valid email address';
    }

    if (formData.reviewer_phone && !/^[\d\s\-+()]+$/.test(formData.reviewer_phone)) {
      newErrors.reviewer_phone = 'Please enter a valid phone number';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const reviewData = {
        review_type: reviewType,
        affiliate_id: businessSlug ? undefined : undefined, // This would need to be resolved from businessSlug
        business_slug: businessSlug,
        rating: formData.rating,
        title: formData.title || undefined,
        content: formData.content,
        reviewer_name: formData.reviewer_name,
        reviewer_email: formData.reviewer_email || undefined,
        reviewer_phone: formData.reviewer_phone || undefined,
        service_category: formData.service_category || undefined,
        service_date: formData.service_date || undefined
      };

      await submitReview(reviewData);
      await onSubmit(formData);
    } catch (error) {
      console.error('Failed to submit review:', error);
    }
  };

  const isSubmitting = isLoading || submitLoading;

  return (
    <div className="bg-stone-800 rounded-xl p-6 border border-stone-600">
      <div className="mb-6">
        <h3 className="text-2xl font-bold text-white mb-2">
          Write a Review
        </h3>
        {businessName && (
          <p className="text-stone-400">
            Share your experience with <span className="text-orange-400 font-medium">{businessName}</span>
          </p>
        )}
      </div>

      <form onSubmit={(e) => { void handleSubmit(e); }} className="space-y-6">
        {/* Rating */}
        <div>
          <label htmlFor="rating" className="block text-stone-300 text-sm font-medium mb-3">
            Overall Rating *
          </label>
          <div className="flex items-center gap-2">
            <StarRating 
              rating={formData.rating} 
              size="lg" 
              showCount={false}
            />
            <span className="text-stone-400 text-sm ml-2">
              {formData.rating} out of 5 stars
            </span>
          </div>
        </div>

        {/* Reviewer Name */}
        <div>
          <label htmlFor="reviewer_name" className="block text-stone-300 text-sm font-medium mb-2">
            Your Name *
          </label>
          <input
            id="reviewer_name"
            type="text"
            value={formData.reviewer_name}
            onChange={(e) => { handleInputChange('reviewer_name', e.target.value); }}
            className={`w-full px-4 py-3 bg-stone-700 border rounded-lg text-white placeholder-stone-400 focus:outline-none focus:ring-2 focus:ring-orange-400 ${
              errors.reviewer_name ? 'border-red-500' : 'border-stone-600'
            }`}
            placeholder="Enter your name"
            disabled={isSubmitting}
          />
          {errors.reviewer_name && (
            <p className="text-red-400 text-sm mt-1">{errors.reviewer_name}</p>
          )}
        </div>

        {/* Review Title */}
        <div>
          <label htmlFor="title" className="block text-stone-300 text-sm font-medium mb-2">
            Review Title (Optional)
          </label>
          <input
            id="title"
            type="text"
            value={formData.title}
            onChange={(e) => { handleInputChange('title', e.target.value); }}
            className="w-full px-4 py-3 bg-stone-700 border border-stone-600 rounded-lg text-white placeholder-stone-400 focus:outline-none focus:ring-2 focus:ring-orange-400"
            placeholder="Summarize your experience"
            disabled={isSubmitting}
          />
        </div>

        {/* Review Content */}
        <div>
          <label htmlFor="content" className="block text-stone-300 text-sm font-medium mb-2">
            Your Review *
          </label>
          <textarea
            id="content"
            value={formData.content}
            onChange={(e) => { handleInputChange('content', e.target.value); }}
            rows={4}
            className={`w-full px-4 py-3 bg-stone-700 border rounded-lg text-white placeholder-stone-400 focus:outline-none focus:ring-2 focus:ring-orange-400 resize-none ${
              errors.content ? 'border-red-500' : 'border-stone-600'
            }`}
            placeholder="Tell us about your experience. What did you like? What could be improved?"
            disabled={isSubmitting}
          />
          <div className="flex justify-between mt-1">
            {errors.content && (
              <p className="text-red-400 text-sm">{errors.content}</p>
            )}
            <p className="text-stone-400 text-sm ml-auto">
              {formData.content.length}/2000 characters
            </p>
          </div>
        </div>

        {/* Service Category */}
        <div>
          <label htmlFor="service_category" className="block text-stone-300 text-sm font-medium mb-2">
            Service Category (Optional)
          </label>
          <select
            id="service_category"
            value={formData.service_category}
            onChange={(e) => { handleInputChange('service_category', e.target.value); }}
            className="w-full px-4 py-3 bg-stone-700 border border-stone-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-orange-400"
            disabled={isSubmitting}
          >
            <option value="">Select a service</option>
            <option value="auto">Auto Detailing</option>
            <option value="boat">Boat Detailing</option>
            <option value="rv">RV Detailing</option>
            <option value="ceramic">Ceramic Coating</option>
            <option value="ppf">Paint Protection Film</option>
            <option value="paint-correction">Paint Correction</option>
            <option value="interior">Interior Detailing</option>
            <option value="exterior">Exterior Detailing</option>
          </select>
        </div>

        {/* Service Date */}
        <div>
          <label htmlFor="service_date" className="block text-stone-300 text-sm font-medium mb-2">
            Service Date (Optional)
          </label>
          <input
            id="service_date"
            type="date"
            value={formData.service_date}
            onChange={(e) => { handleInputChange('service_date', e.target.value); }}
            className="w-full px-4 py-3 bg-stone-700 border border-stone-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-orange-400"
            disabled={isSubmitting}
          />
        </div>

        {/* Contact Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label htmlFor="reviewer_email" className="block text-stone-300 text-sm font-medium mb-2">
              Email (Optional)
            </label>
            <input
              id="reviewer_email"
              type="email"
              value={formData.reviewer_email}
              onChange={(e) => { handleInputChange('reviewer_email', e.target.value); }}
              className={`w-full px-4 py-3 bg-stone-700 border rounded-lg text-white placeholder-stone-400 focus:outline-none focus:ring-2 focus:ring-orange-400 ${
                errors.reviewer_email ? 'border-red-500' : 'border-stone-600'
              }`}
              placeholder="your@email.com"
              disabled={isSubmitting}
            />
            {errors.reviewer_email && (
              <p className="text-red-400 text-sm mt-1">{errors.reviewer_email}</p>
            )}
          </div>

          <div>
            <label htmlFor="reviewer_phone" className="block text-stone-300 text-sm font-medium mb-2">
              Phone (Optional)
            </label>
            <input
              id="reviewer_phone"
              type="tel"
              value={formData.reviewer_phone}
              onChange={(e) => { handleInputChange('reviewer_phone', e.target.value); }}
              className={`w-full px-4 py-3 bg-stone-700 border rounded-lg text-white placeholder-stone-400 focus:outline-none focus:ring-2 focus:ring-orange-400 ${
                errors.reviewer_phone ? 'border-red-500' : 'border-stone-600'
              }`}
              placeholder="(555) 123-4567"
              disabled={isSubmitting}
            />
            {errors.reviewer_phone && (
              <p className="text-red-400 text-sm mt-1">{errors.reviewer_phone}</p>
            )}
          </div>
        </div>

        {/* Submit Buttons */}
        <div className="flex gap-4 pt-4">
          <button
            type="submit"
            disabled={isSubmitting}
            className="flex-1 bg-orange-400 text-stone-900 font-semibold py-3 px-6 rounded-lg hover:bg-orange-300 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isSubmitting ? 'Submitting...' : 'Submit Review'}
          </button>
          {onCancel && (
            <button
              type="button"
              onClick={onCancel}
              disabled={isSubmitting}
              className="px-6 py-3 border border-stone-600 text-stone-300 rounded-lg hover:bg-stone-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Cancel
            </button>
          )}
        </div>

        <p className="text-stone-400 text-xs text-center">
          Your review will be moderated before being published. We reserve the right to edit or remove reviews that violate our community guidelines.
        </p>
      </form>
    </div>
  );
};

import { Calendar, ExternalLink, X } from 'lucide-react';
import React, { useEffect, useState } from 'react';

import { ReviewSourceIcon } from './ReviewSourceIcon';
import { StarRating } from './StarRating';
import type { Review } from './types';

interface ReviewModalProps {
  review: Review;
  isOpen: boolean;
  onClose: () => void;
}

export const ReviewModal: React.FC<ReviewModalProps> = ({ 
  review, 
  isOpen, 
  onClose 
}) => {
  const [imageError, setImageError] = useState(false);

  // Handle escape key
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
      document.body.style.overflow = 'hidden'; // Prevent background scrolling
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleClose = () => {
    onClose();
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center p-4" style={{ zIndex: 999999 }}>
      {/* Backdrop */}
      <div 
        className="absolute inset-0 bg-black bg-opacity-75 backdrop-blur-sm transition-opacity"
        onClick={handleClose}
        onKeyDown={(e) => {
          if (e.key === 'Enter' || e.key === ' ') {
            e.preventDefault();
            handleClose();
          }
        }}
        role="button"
        tabIndex={0}
        aria-label="Close modal"
      />
      
      {/* Modal */}
      <div 
        className="relative w-full max-w-2xl max-h-[90vh] overflow-hidden rounded-2xl bg-stone-900 text-left shadow-2xl transform transition-all animate-in fade-in-0 zoom-in-95 duration-300"
        role="dialog"
        aria-modal="true"
        aria-labelledby="modal-title"
      >
        {/* Header */}
        <div className="flex items-center justify-between border-b border-stone-600 px-6 py-4">
          <h3 id="modal-title" className="text-xl font-semibold text-white">Review Details</h3>
          <button
            onClick={handleClose}
            className="rounded-lg p-2 text-stone-400 hover:bg-stone-700 hover:text-white transition-colors"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Content */}
        <div className="px-6 py-6 overflow-y-auto max-h-[calc(90vh-140px)]">
          {/* Reviewer Info */}
          <div className="flex items-start gap-4 mb-6">
            {/* Profile Image */}
            {review.reviewerUrl ? (
              <a
                href={review.reviewerUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="w-16 h-16 rounded-full bg-gradient-to-br from-orange-400 to-orange-500 flex items-center justify-center text-white font-bold text-3xl overflow-hidden hover:from-orange-300 hover:to-orange-400 transition-all duration-200 cursor-pointer group"
                title={`View ${review.customerName}'s profile`}
              >
                {review.profileImage && !imageError ? (
                  <img 
                    src={review.profileImage} 
                    alt={review.customerName}
                    className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-200"
                    onError={() => { setImageError(true); }}
                  />
                ) : (
                  review.customerName.charAt(0).toUpperCase()
                )}
              </a>
            ) : (
              <div className="w-16 h-16 rounded-full bg-gradient-to-br from-orange-400 to-orange-500 flex items-center justify-center text-white font-bold text-3xl overflow-hidden">
                {review.profileImage && !imageError ? (
                  <img 
                    src={review.profileImage} 
                    alt={review.customerName}
                    className="w-full h-full object-cover"
                    onError={() => { setImageError(true); }}
                  />
                ) : (
                  review.customerName.charAt(0).toUpperCase()
                )}
              </div>
            )}

            {/* Reviewer Details */}
            <div className="flex-1">
              <div className="flex items-center gap-3 mb-2">
                {review.reviewerUrl ? (
                  <a
                    href={review.reviewerUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-xl font-semibold text-white hover:text-orange-400 transition-colors duration-200 cursor-pointer group flex items-center gap-2"
                    title={`View ${review.customerName}'s profile`}
                  >
                    {review.customerName}
                    <ExternalLink className="w-4 h-4 opacity-0 group-hover:opacity-100 transition-opacity duration-200" />
                  </a>
                ) : (
                  <h4 className="text-xl font-semibold text-white">
                    {review.customerName}
                  </h4>
                )}
                {review.isVerified && (
                  <span className="text-orange-400 text-sm font-medium bg-orange-400/10 px-2 py-1 rounded">
                    Verified
                  </span>
                )}
              </div>

              <div className="flex items-center gap-4 text-stone-400 text-sm">
                <div className="flex items-center gap-1">
                  <Calendar className="w-4 h-4" />
                  <span>{formatDate(review.date)}</span>
                </div>

                {review.reviewSource && (
                  <div className="flex items-center gap-1">
                    <ReviewSourceIcon source={review.reviewSource} size="sm" />
                    <span className="capitalize">{review.reviewSource}</span>
                  </div>
                )}
              </div>

              {/* Service Category */}
              {review.serviceCategory && (
                <div className="mt-2">
                  <span className="text-stone-400 text-sm bg-stone-700 px-3 py-1 rounded-full">
                    {review.serviceCategory}
                  </span>
                </div>
              )}
            </div>
          </div>

          {/* Review Title */}
          {review.title && (
            <h5 className="text-lg font-semibold text-white mb-4">
              {review.title}
            </h5>
          )}

          {/* Full Review Text */}
          <div className="mb-6">
            <p className="text-gray-300 leading-relaxed text-base">
              &ldquo;{review.reviewText}&rdquo;
            </p>
          </div>


          {/* Rating Display */}
          <div className="border-t border-stone-600 pt-4">
            <div className="flex items-center justify-center gap-2">
              <StarRating rating={review.rating} size="md" />
              <span className="text-stone-400 text-sm ml-2">{review.rating}/5</span>
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="border-t border-stone-600 px-6 py-4 bg-stone-900">
          <div className="flex justify-end gap-3">
            <button
              onClick={handleClose}
              className="px-6 py-2 bg-orange-400 text-stone-900 font-medium rounded-lg hover:bg-orange-300 transition-colors"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

import React, { useState } from 'react';

import { useReviews } from './hooks/useReviews';
import type { DatabaseReview, ReviewUpdate } from './types';

interface ReviewModerationProps {
  isAdmin?: boolean;
}

export const ReviewModeration: React.FC<ReviewModerationProps> = ({ isAdmin = false }) => {
  const [selectedReview, setSelectedReview] = useState<DatabaseReview | null>(null);
  const [moderationNotes, setModerationNotes] = useState('');
  const [isUpdating, setIsUpdating] = useState(false);

  // Fetch pending reviews for moderation
  const { 
    reviews: pendingReviews, 
    loading, 
    error, 
    refetch 
  } = useReviews({
    type: 'mdh', // Could be expanded to include affiliate reviews
    status: 'pending',
    limit: 20
  });

  const handleApprove = async (review: DatabaseReview): Promise<void> => {
    if (!isAdmin) return;
    
    setIsUpdating(true);
    try {
      const updateData: ReviewUpdate = {
        status: 'approved',
        moderation_notes: moderationNotes || undefined
      };

      const response = await fetch(`/api/reviews/${String(review.id)}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token') ?? ''}` // In production, use proper auth
        },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) {
        throw new Error('Failed to approve review');
      }

      setSelectedReview(null);
      setModerationNotes('');
      refetch();
    } catch (error) {
      console.error('Error approving review:', error);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleReject = async (review: DatabaseReview): Promise<void> => {
    if (!isAdmin) return;
    
    setIsUpdating(true);
    try {
      const updateData: ReviewUpdate = {
        status: 'rejected',
        moderation_notes: moderationNotes || 'Review rejected by moderator'
      };

      const response = await fetch(`/api/reviews/${String(review.id)}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token') ?? ''}` // In production, use proper auth
        },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) {
        throw new Error('Failed to reject review');
      }

      setSelectedReview(null);
      setModerationNotes('');
      refetch();
    } catch (error) {
      console.error('Error rejecting review:', error);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleFeature = async (review: DatabaseReview): Promise<void> => {
    if (!isAdmin) return;
    
    setIsUpdating(true);
    try {
      const updateData: ReviewUpdate = {
        is_featured: !review.is_featured
      };

      const response = await fetch(`/api/reviews/${String(review.id)}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token') ?? ''}` // In production, use proper auth
        },
        body: JSON.stringify(updateData)
      });

      if (!response.ok) {
        throw new Error('Failed to update review');
      }

      refetch();
    } catch (error) {
      console.error('Error updating review:', error);
    } finally {
      setIsUpdating(false);
    }
  };

  if (!isAdmin) {
    return null;
  }

  if (loading) {
    return (
      <div className="bg-stone-800 rounded-xl p-6">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-400 mx-auto mb-4"></div>
          <p className="text-stone-300">Loading reviews for moderation...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-stone-800 rounded-xl p-6">
        <div className="text-center text-red-400">
          <p>Error loading reviews: {error}</p>
          <button 
            onClick={refetch}
            className="mt-2 bg-orange-400 text-stone-900 px-4 py-2 rounded hover:bg-orange-300 transition-colors"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-stone-800 rounded-xl p-6">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-bold text-white">Review Moderation</h2>
        <span className="bg-orange-400 text-stone-900 px-3 py-1 rounded-full text-sm font-medium">
          {pendingReviews.length} pending
        </span>
      </div>

      {pendingReviews.length === 0 ? (
        <div className="text-center py-8">
          <p className="text-stone-400">No pending reviews to moderate</p>
        </div>
      ) : (
        <div className="space-y-4">
          {pendingReviews.map((review) => (
            <div key={review.id} className="bg-stone-700 rounded-lg p-4 border border-stone-600">
              <div className="flex items-start justify-between mb-3">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <h3 className="text-white font-semibold">{review.reviewer_name}</h3>
                    <div className="flex items-center gap-1">
                      {Array.from({ length: 5 }, (_, i) => (
                        <span
                          key={i}
                          className={`text-lg ${
                            i < review.rating ? 'text-yellow-400' : 'text-stone-500'
                          }`}
                        >
                          ‚òÖ
                        </span>
                      ))}
                    </div>
                    {review.is_verified && (
                      <span className="text-orange-400 text-xs bg-orange-400/20 px-2 py-1 rounded">
                        Verified
                      </span>
                    )}
                  </div>
                  
                  {review.title && (
                    <h4 className="text-stone-300 font-medium mb-2">{review.title}</h4>
                  )}
                  
                  <p className="text-stone-300 text-sm leading-relaxed mb-3">
                    {review.content}
                  </p>
                  
                  <div className="flex items-center gap-4 text-xs text-stone-400">
                    <span>Submitted: {new Date(String(review.created_at)).toLocaleDateString()}</span>
                    {review.service_category && (
                      <span>Service: {review.service_category}</span>
                    )}
                    {review.business_name && (
                      <span>Business: {review.business_name}</span>
                    )}
                  </div>
                </div>
              </div>

              <div className="flex items-center gap-3 pt-3 border-t border-stone-600">
                <button
                  onClick={() => void handleApprove(review)}
                  disabled={isUpdating}
                  className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded text-sm font-medium transition-colors disabled:opacity-50"
                >
                  Approve
                </button>
                <button
                  onClick={() => void handleReject(review)}
                  disabled={isUpdating}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded text-sm font-medium transition-colors disabled:opacity-50"
                >
                  Reject
                </button>
                <button
                  onClick={() => void handleFeature(review)}
                  disabled={isUpdating}
                  className={`px-4 py-2 rounded text-sm font-medium transition-colors disabled:opacity-50 ${
                    review.is_featured
                      ? 'bg-orange-400 text-stone-900 hover:bg-orange-300'
                      : 'bg-stone-600 text-stone-300 hover:bg-stone-500'
                  }`}
                >
                  {review.is_featured ? 'Featured' : 'Feature'}
                </button>
                <button
                  onClick={() => { setSelectedReview(review); }}
                  className="bg-stone-600 hover:bg-stone-500 text-stone-300 px-4 py-2 rounded text-sm font-medium transition-colors"
                >
                  View Details
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Review Details Modal */}
      {selectedReview && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex min-h-full items-center justify-center p-4">
            <div 
              className="fixed inset-0 bg-black bg-opacity-50" 
              onClick={() => { setSelectedReview(null); }}
              onKeyDown={(e) => {
                if (e.key === 'Escape') {
                  setSelectedReview(null);
                }
              }}
              role="button"
              tabIndex={0}
              aria-label="Close modal"
            />
            <div className="relative bg-stone-800 rounded-xl p-6 max-w-2xl w-full">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-bold text-white">Review Details</h3>
                <button
                  onClick={() => { setSelectedReview(null); }}
                  className="text-stone-400 hover:text-white"
                >
                  ‚úï
                </button>
              </div>
              
              <div className="space-y-4">
                <div>
                  <label htmlFor="moderation-notes" className="block text-stone-300 text-sm font-medium mb-2">
                    Moderation Notes
                  </label>
                  <textarea
                    id="moderation-notes"
                    value={moderationNotes}
                    onChange={(e) => { setModerationNotes(e.target.value); }}
                    rows={3}
                    className="w-full px-3 py-2 bg-stone-700 border border-stone-600 rounded text-white placeholder-stone-400 focus:outline-none focus:ring-2 focus:ring-orange-400"
                    placeholder="Add notes about this review..."
                  />
                </div>
                
                <div className="flex gap-3">
                  <button
                    onClick={() => void handleApprove(selectedReview)}
                    disabled={isUpdating}
                    className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded font-medium transition-colors disabled:opacity-50"
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => void handleReject(selectedReview)}
                    disabled={isUpdating}
                    className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded font-medium transition-colors disabled:opacity-50"
                  >
                    Reject
                  </button>
                  <button
                    onClick={() => { setSelectedReview(null); }}
                    className="bg-stone-600 hover:bg-stone-500 text-stone-300 px-4 py-2 rounded font-medium transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

import { ChevronLeft, ChevronRight } from 'lucide-react';
import React, { useMemo, useState } from 'react';

import { useReviews } from './hooks/useReviews';
import { ReviewCard } from './ReviewCard';
import { ReviewModal } from './ReviewModal';
import { ReviewsHeader } from './ReviewsHeader';
import type { Review, ReviewsProps } from './types';

export const Reviews: React.FC<ReviewsProps> = ({ 
  reviews: propReviews,
  maxReviews = 3,
  reviewType = 'mdh',
  businessSlug,
  featuredOnly = false,
  verifiedOnly = false
}) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedReview, setSelectedReview] = useState<Review | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Memoize the params object to prevent infinite loops
  const reviewParams = useMemo(() => ({
    type: reviewType,
    business_slug: businessSlug,
    featured_only: featuredOnly,
    verified_only: verifiedOnly,
    limit: 50 // Fetch more than needed for carousel
  }), [reviewType, businessSlug, featuredOnly, verifiedOnly]);

  // Fetch reviews from API if not provided as props
  const { 
    reviews: apiReviews, 
    loading, 
    error 
  } = useReviews(propReviews ? {} : reviewParams); // Don't fetch if we have prop reviews

  // Use prop reviews if provided, otherwise use API reviews
  const reviews = propReviews || apiReviews;

  // Sort reviews: featured first, then 5-star reviews, then by date (newest first)
  const sortedReviews = useMemo(() => {
    if (!reviews.length) return [];

    return [...reviews].sort((a, b) => {
      // First, sort by featured status
      if (a.isFeatured !== b.isFeatured) {
        return b.isFeatured ? 1 : -1;
      }
      // Then, sort by rating (5 stars first)
      if (a.rating !== b.rating) {
        return b.rating - a.rating;
      }
      // Finally, sort by date (newest first)
      return new Date(b.date).getTime() - new Date(a.date).getTime();
    });
  }, [reviews]);

  const displayedReviews = sortedReviews.slice(currentIndex, currentIndex + maxReviews);
  const canGoLeft = currentIndex > 0;
  const canGoRight = currentIndex + maxReviews < sortedReviews.length;

  const handlePrevious = () => {
    if (canGoLeft) {
      setCurrentIndex(Math.max(0, currentIndex - 1));
    }
  };

  const handleNext = () => {
    if (canGoRight) {
      setCurrentIndex(Math.min(sortedReviews.length - maxReviews, currentIndex + 1));
    }
  };

  const handleReviewClick = (review: Review) => {
    setSelectedReview(review);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedReview(null);
  };

  // Show loading state
  if (loading) {
    return (
      <section className="bg-stone-800 py-20 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-orange-400 mx-auto mb-4"></div>
            <p className="text-stone-300">Loading reviews...</p>
          </div>
        </div>
      </section>
    );
  }

  // Show error state
  if (error) {
    return (
      <section className="bg-stone-800 py-20 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center">
            <div className="text-red-400 mb-4">
              <svg className="w-12 h-12 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-stone-300 mb-2">Failed to load reviews</h3>
            <p className="text-stone-400 mb-4">{error}</p>
            <button 
              onClick={() => { window.location.reload(); }} 
              className="bg-orange-400 text-stone-900 px-6 py-2 rounded-lg hover:bg-orange-300 transition-colors"
            >
              Try Again
            </button>
          </div>
        </div>
      </section>
    );
  }

  // Show empty state
  if (!sortedReviews.length) {
    return (
      <section className="bg-stone-800 py-20 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center">
            <div className="text-stone-400 mb-4">
              <svg className="w-12 h-12 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-stone-300 mb-2">No reviews yet</h3>
            <p className="text-stone-400">Be the first to share your experience!</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="bg-stone-800 py-20 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        {/* Header Section */}
        <ReviewsHeader reviews={sortedReviews} />

        {/* Reviews Carousel */}
        <div className="relative mb-12">
          {/* Navigation Arrows */}
          <button
            onClick={handlePrevious}
            disabled={!canGoLeft}
            className={`absolute left-2 md:-left-12 top-1/2 -translate-y-1/2 z-10 transition-all duration-300 ${
              canGoLeft
                ? 'text-orange-400 hover:text-orange-300 hover:scale-125'
                : 'text-stone-500 cursor-not-allowed'
            }`}
            aria-label="Previous reviews"
          >
            <ChevronLeft className="w-6 h-6 md:w-8 md:h-8" />
          </button>

          <button
            onClick={handleNext}
            disabled={!canGoRight}
            className={`absolute right-2 md:-right-12 top-1/2 -translate-y-1/2 z-10 transition-all duration-300 ${
              canGoRight
                ? 'text-orange-400 hover:text-orange-300 hover:scale-125'
                : 'text-stone-500 cursor-not-allowed'
            }`}
            aria-label="Next reviews"
          >
            <ChevronRight className="w-6 h-6 md:w-8 md:h-8" />
          </button>

          {/* Reviews Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-8 transition-all duration-500 ease-in-out">
            {displayedReviews.map((review, index) => (
              <div
                key={review.id}
                className="transform transition-all duration-500 ease-in-out"
                style={{
                  animationDelay: `${(index * 100).toString()}ms`,
                }}
              >
                <ReviewCard 
                  review={review} 
                  onReviewClick={handleReviewClick}
                />
              </div>
            ))}
          </div>

          {/* Pagination Dots */}
          <div className="flex justify-center mt-8 gap-2">
            {Array.from({ length: Math.ceil(sortedReviews.length / maxReviews) }, (_, index) => {
              const isActive = Math.floor(currentIndex / maxReviews) === index;
              return (
                <button
                  key={index}
                  onClick={() => { setCurrentIndex(index * maxReviews); }}
                  className={`w-3 h-3 rounded-full transition-all duration-300 ${
                    isActive
                      ? 'bg-orange-400 scale-125'
                      : 'bg-stone-500 hover:bg-stone-400'
                  }`}
                  aria-label={`Go to page ${(index + 1).toString()}`}
                />
              );
            })}
          </div>
        </div>

      </div>

      {/* Review Modal - Rendered at root level */}
      {selectedReview && (
        <ReviewModal
          review={selectedReview}
          isOpen={isModalOpen}
          onClose={handleCloseModal}
        />
      )}
    </section>
  );
};

