              {/* Social Media Icons */}
              {socialMedia && (
                <div className="p-4 border-t border-stone-700">
                  <div className="text-white text-sm mb-3">Follow Us</div>
                  <SocialMediaIcons socialMedia={socialMedia} />
                </div>
              )}
            </div>
          </div>
        </>
      )}
