      }
    } finally {
      setLoading(false);
    }
  };

  const handleToggleMode = () => {
    setIsLogin(!isLogin);
    setError('');
    setRateLimitInfo(null);
    setCountdown(0);
  };

  const handleClose = useCallback(() => {
    setError('');
    setRateLimitInfo(null);
    setCountdown(0);
    onClose();
  }, [onClose]);

  // Handle keyboard navigation and focus trapping
  const handleKeyDown = useCallback((event: KeyboardEvent) => {
    if (event.key === 'Escape') {
      event.preventDefault();
      handleClose();
    }
    
    // Focus trapping: Tab key navigation
    if (event.key === 'Tab') {
      if (!modalRef.current) return;
      
      const focusableElements = modalRef.current.querySelectorAll(
        'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
      );
      
      if (focusableElements.length === 0) return;
      
      const firstElement = focusableElements[0] as HTMLElement;
      const lastElement = focusableElements[focusableElements.length - 1] as HTMLElement;
      
      if (event.shiftKey) {
        // Shift + Tab: move backwards
        if (document.activeElement === firstElement) {
          event.preventDefault();
          lastElement.focus();
        }
      } else {
        // Tab: move forwards
        if (document.activeElement === lastElement) {
          event.preventDefault();
          firstElement.focus();
        }
      }
    }
  }, [handleClose]);

  // Focus management
  useEffect(() => {
    if (isOpen) {
      // Focus the first focusable element when modal opens
      setTimeout(() => {
        if (firstFocusableRef.current) {
          firstFocusableRef.current.focus();
        }
      }, 100);
      
      // Prevent body scroll when modal is open
      document.body.style.overflow = 'hidden';
      
      // Add event listeners for accessibility
      document.addEventListener('keydown', handleKeyDown);
      
      return () => {
        document.removeEventListener('keydown', handleKeyDown);
        document.body.style.overflow = 'unset';
      };
    }
  }, [isOpen, handleClose, handleKeyDown]);

  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
    return () => { setMounted(false); };
  }, []);

  if (!isOpen || !mounted) return null;

  // Format countdown display
  const formatCountdown = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return createPortal(
    <div 
      className="fixed inset-0 z-50 flex items-center justify-center p-4"
      role="dialog"
      aria-modal="true"
      aria-labelledby="login-modal-title"
      aria-describedby="login-modal-description"
    >
      {/* Backdrop */}
      <div 
        className="absolute inset-0 bg-black/70 backdrop-blur-sm transition-opacity duration-300"
        onClick={handleClose}
        onKeyDown={(e) => {
          if (e.key === 'Escape' || e.key === 'Enter' || e.key === ' ') {
            e.preventDefault();
            handleClose();
          }
        }}
        role="button"
        tabIndex={0}
        aria-label="Close modal"
        onMouseDown={(e) => { e.preventDefault(); }}
        onMouseUp={(e) => { e.preventDefault(); }}
      />
      
      {/* Modal */}
      <div 
        ref={modalRef}
        className="relative w-full max-w-md transform transition-all duration-300 scale-100"
        role="dialog"
        aria-modal="true"
      >
        <div 
          className="bg-stone-900 rounded-2xl shadow-2xl border border-stone-700 overflow-hidden"
          role="document"
        >
          {/* Header */}
          <ModalHeader 
            isLogin={isLogin} 
            onClose={handleClose}
            ref={firstFocusableRef}
          />

          {/* Error Display */}
          {error && (
            <div 
              className="mx-8 mb-4 bg-red-900/50 border border-red-700 text-red-300 px-4 py-3 rounded-xl text-sm"
              role="alert"
              aria-live="polite"
              id="login-error-message"
            >
              {error}
              {rateLimitInfo && countdown > 0 && (
                <div className="mt-2 text-center">
                  <div className="text-lg font-mono font-bold text-orange-400">
                    {formatCountdown(countdown)}
                  </div>
                  <div className="text-xs text-red-200">
                    Try again in {countdown} seconds
                  </div>
                </div>
              )}
            </div>
          )}

          {/* Form */}
          {isLogin ? (
            <LoginForm 
              onSubmit={handleLogin}
              loading={loading}
              error={error}
              disabled={rateLimitInfo !== null && countdown > 0}
            />
          ) : (
            <RegisterForm 
              onSubmit={handleRegister}
              loading={loading}
              error={error}
              disabled={rateLimitInfo !== null && countdown > 0}
            />
          )}

          {/* Social Login */}
          <SocialLogin />

          {/* Toggle Mode */}
          <ToggleMode 
            isLogin={isLogin} 
            onToggle={handleToggleMode}
            ref={lastFocusableRef}
          />
        </div>
      </div>
    </div>,
    document.body
  );
};

export default LoginModal;

