/**
 * Service Worker for Mobile Detail Hub PWA
 * Provides basic caching for assets and offline functionality
 */

const CACHE_NAME = 'mdh-v1.0.0';
const STATIC_CACHE_URLS = [
  '/',
  '/manifest.webmanifest',
  '/assets/favicon.webp',
  '/assets/icon-192.webp', 
  '/assets/icon-512.webp',
  '/hero/image1-lg.webp',
  '/hero/image2-lg.webp'
];

// Install event - cache static assets
self.addEventListener('install', (event) => {
      // Service Worker installing

  event.waitUntil(
    caches.open(CACHE_NAME)
      .then((cache) => {
        // Caching static assets
        return cache.addAll(STATIC_CACHE_URLS);
      })
      .then(() => {
                  // Static assets cached successfully
        return self.skipWaiting();
      })
      .catch((error) => {
        // Failed to cache static assets
      })
  );
});

// Activate event - clean up old caches
self.addEventListener('activate', (event) => {
      // Service Worker activating

  event.waitUntil(
    caches.keys()
      .then((cacheNames) => {
        return Promise.all(
          cacheNames.map((cacheName) => {
            if (cacheName !== CACHE_NAME) {
              // Deleting old cache
              return caches.delete(cacheName);
            }
          })
        );
      })
      .then(() => {
        // Service Worker activated
        return self.clients.claim();
      })
  );
});

// Fetch event - serve from cache, fallback to network
self.addEventListener('fetch', (event) => {
  // Only handle GET requests
  if (event.request.method !== 'GET') {
    return;
  }

  // Skip cross-origin requests
  if (!event.request.url.startsWith(self.location.origin)) {
    return;
  }

  event.respondWith(
    caches.match(event.request)
      .then((cachedResponse) => {
        if (cachedResponse) {
          return cachedResponse;
        }

        // Network first for HTML pages, cache first for assets
        if (event.request.destination === 'document') {
          return networkFirst(event.request);
        } else {
          return cacheFirst(event.request);
        }
      })
      .catch(() => {
        // Offline fallback for HTML pages
        if (event.request.destination === 'document') {
          return caches.match('/');
        }
      })
  );
});

/**
 * Network first strategy - for HTML pages
 */
async function networkFirst(request) {
  try {
    const networkResponse = await fetch(request);

    if (networkResponse.ok) {
      const cache = await caches.open(CACHE_NAME);
      cache.put(request, networkResponse.clone());
    }

    return networkResponse;
  } catch (error) {
    const cachedResponse = await caches.match(request);
    return cachedResponse || new Response('Offline', { status: 503 });
  }
}

/**
 * Cache first strategy - for static assets
 */
async function cacheFirst(request) {
  const cachedResponse = await caches.match(request);

  if (cachedResponse) {
    return cachedResponse;
  }

  try {
    const networkResponse = await fetch(request);

    if (networkResponse.ok) {
      const cache = await caches.open(CACHE_NAME);
      cache.put(request, networkResponse.clone());
    }

    return networkResponse;
  } catch (error) {
    return new Response('Asset unavailable', { status: 503 });
  }
}


