  shutdownStatus = status;
};



// Liveness endpoint - only checks if process is responsive
// Always returns 200 if event loop is working (for Kubernetes/container orchestration)
router.get('/live', (req, res) => {
  res.status(200).json({
    status: 'alive',
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
    pid: process.pid,
    memory: {
      used: Math.round(process.memoryUsage().heapUsed / 1024 / 1024),
      total: Math.round(process.memoryUsage().heapTotal / 1024 / 1024),
      external: Math.round(process.memoryUsage().external / 1024 / 1024)
    }
  });
});

// Readiness endpoint - checks if service is ready to receive traffic
router.get('/ready', asyncHandler(async (req, res) => {
  let dbReady = false;
  let dbError = null;
  
  try {
    // Quick database ping with 250ms timeout
    const timeoutPromise = new Promise((_, reject) => 
      setTimeout(() => reject(new Error('Database readiness timeout')), 250)
    );
    
    await Promise.race([
      pool.query('SELECT 1'),
      timeoutPromise
    ]);
    
    dbReady = true;
  } catch (error) {
    dbError = error.message;
  }
  
  if (dbReady) {
    res.status(200).json({
      status: 'ready',
      timestamp: new Date().toISOString(),
      database: {
        connected: true
      }
    });
  } else {
    res.status(503).json({
      status: 'not_ready',
      timestamp: new Date().toISOString(),
      database: {
        connected: false,
        error: dbError
      }
    });
  }
}));

