const cookie = require('cookie');
const { verifyAccessToken, isTokenBlacklisted } = require('../utils/tokenManager');
const logger = require('../utils/logger');

// Authentication Middleware
const authenticateToken = async (req, res, next) => {
  try {
    logger.debug('Authentication middleware called', { 
      path: req.path, 
      method: req.method,
      ip: req.ip
    });
    
    // 1) Prefer HttpOnly cookie
    const cookies = req.headers.cookie ? cookie.parse(req.headers.cookie) : {};
    const cookieToken = cookies['access_token'];

    // 2) Fallback to Authorization header
    const authHeader = req.headers['authorization'];
    const headerToken = authHeader && authHeader.split(' ')[1];

    const token = cookieToken || headerToken;
    
    if (!token) {
      logger.debug('No authentication token provided', { 
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(401).json({ 
        error: 'Access token required',
        code: 'NO_TOKEN',
        message: 'Please provide a valid access token'
      });
    }
    
    logger.debug('Token found, verifying...', { 
      tokenSource: cookieToken ? 'cookie' : 'header',
      path: req.path
    });

    // Check if token is blacklisted
    if (isTokenBlacklisted(token)) {
      logger.warn('Blacklisted token used', { 
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(401).json({ 
        error: 'Token has been revoked',
        code: 'TOKEN_REVOKED',
        message: 'This token is no longer valid'
      });
    }

    // Verify the token
    const user = verifyAccessToken(token);
    req.user = user;
    
    logger.debug('Authentication successful', { 
      userId: user.userId, 
      email: user.email,
      path: req.path
    });
    
    next();
  } catch (error) {
    if (error.message === 'Access token expired') {
      logger.debug('Access token expired', { 
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(401).json({ 
        error: 'Token expired',
        code: 'TOKEN_EXPIRED',
        message: 'Please refresh your token'
      });
    }
    if (error.message === 'Invalid access token') {
      logger.warn('Invalid access token provided', { 
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(403).json({ 
        error: 'Invalid token',
        code: 'INVALID_TOKEN',
        message: 'The provided token is not valid'
      });
    }
    
    logger.error('Authentication error:', { 
      error: error.message, 
      path: req.path, 
      method: req.method,
      ip: req.ip
    });
    return res.status(500).json({ 
      error: 'Authentication failed',
      code: 'AUTH_ERROR',
      message: 'An error occurred during authentication'
    });
  }
};

