const cookie = require('cookie');
const { verifyAccessToken, isTokenBlacklisted } = require('../utils/tokenManager');
const logger = require('../utils/logger');

// Authentication Middleware
const authenticateToken = async (req, res, next) => {
  try {
    logger.debug('Authentication middleware called', { 
      path: req.path, 
      method: req.method,
      ip: req.ip
    });
    
    // 1) Prefer HttpOnly cookie
    const cookies = req.headers.cookie ? cookie.parse(req.headers.cookie) : {};
    const cookieToken = cookies['access_token'];

    // 2) Fallback to Authorization header
    const authHeader = req.headers['authorization'];
    const headerToken = authHeader && authHeader.split(' ')[1];

    const token = cookieToken || headerToken;
    
    if (!token) {
      logger.debug('No authentication token provided', { 
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(401).json({ 
        error: 'Access token required',
        code: 'NO_TOKEN',
        message: 'Please provide a valid access token'
      });
    }
    
    logger.debug('Token found, verifying...', { 
      tokenSource: cookieToken ? 'cookie' : 'header',
      path: req.path
    });

    // Check if token is blacklisted
    if (isTokenBlacklisted(token)) {
      logger.warn('Blacklisted token used', { 
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(401).json({ 
        error: 'Token has been revoked',
        code: 'TOKEN_REVOKED',
        message: 'This token is no longer valid'
      });
    }

    // Verify the token
    const user = verifyAccessToken(token);
    req.user = user;
    
    logger.debug('Authentication successful', { 
      userId: user.userId, 
      email: user.email,
      path: req.path
    });
    
    next();
  } catch (error) {
    if (error.message === 'Access token expired') {
      logger.debug('Access token expired', { 
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(401).json({ 
        error: 'Token expired',
        code: 'TOKEN_EXPIRED',
        message: 'Please refresh your token'
      });
    }
    if (error.message === 'Invalid access token') {
      logger.warn('Invalid access token provided', { 
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(403).json({ 
        error: 'Invalid token',
        code: 'INVALID_TOKEN',
        message: 'The provided token is not valid'
      });
    }
    
    logger.error('Authentication error:', { 
      error: error.message, 
      path: req.path, 
      method: req.method,
      ip: req.ip
    });
    return res.status(500).json({ 
      error: 'Authentication failed',
      code: 'AUTH_ERROR',
      message: 'An error occurred during authentication'
    });
  }
};

// Admin Middleware - Role-aware and future-proof
const requireAdmin = (req, res, next) => {
  logger.debug('Admin middleware called', { 
    path: req.path, 
    method: req.method,
    userId: req.user?.userId,
    ip: req.ip
  });
  
  if (!req.user) {
    logger.warn('Admin access attempt without user context', { 
      path: req.path, 
      method: req.method,
      ip: req.ip
    });
    return res.status(401).json({ 
      error: 'Authentication required',
      code: 'NO_USER_CONTEXT',
      message: 'User must be authenticated to access admin resources'
    });
  }
  
  // Check both isAdmin boolean and roles array for admin validation
  const roles = Array.isArray(req.user.roles) ? req.user.roles : [];
  const isAdminUser = req.user.isAdmin === true || roles.includes('admin');
  
  if (!isAdminUser) {
    logger.warn('Admin access denied', { 
      userId: req.user.userId, 
      email: req.user.email,
      isAdmin: req.user.isAdmin,
      roles: roles,
      path: req.path,
      method: req.method,
      ip: req.ip
    });
    return res.status(403).json({ 
      error: 'Admin access required',
      code: 'INSUFFICIENT_PRIVILEGES',
      message: 'This action requires administrator privileges'
    });
  }
  
  logger.debug('Admin access granted', { 
    userId: req.user.userId, 
    email: req.user.email,
    isAdmin: req.user.isAdmin,
    roles: roles,
    path: req.path
  });
  
  next();
};

// Role-based middleware factory for future extensibility
const requireRole = (role) => {
  return (req, res, next) => {
    logger.debug('Role middleware called', { 
      requiredRole: role,
      path: req.path, 
      method: req.method,
      userId: req.user?.userId,
      ip: req.ip
    });
    
    if (!req.user) {
      logger.warn('Role access attempt without user context', { 
        requiredRole: role,
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(401).json({ 
        error: 'Authentication required',
        code: 'NO_USER_CONTEXT',
        message: 'User must be authenticated to access this resource'
      });
    }
    
    const roles = Array.isArray(req.user.roles) ? req.user.roles : [];
    const hasRole = roles.includes(role) || (role === 'admin' && req.user.isAdmin === true);
    
    if (!hasRole) {
      logger.warn('Role access denied', { 
        userId: req.user.userId, 
        email: req.user.email,
        requiredRole: role,
        userRoles: roles,
        isAdmin: req.user.isAdmin,
        path: req.path,
        method: req.method,
        ip: req.ip
      });
      return res.status(403).json({ 
        error: `${role} access required`,
        code: 'INSUFFICIENT_PRIVILEGES',
        message: `This action requires ${role} privileges`
      });
    }
    
    logger.debug('Role access granted', { 
      userId: req.user.userId, 
      email: req.user.email,
      requiredRole: role,
      userRoles: roles,
      path: req.path
    });
    
    next();
  };
};

// Permission-based middleware factory for fine-grained access control
const requirePermission = (permission) => {
  return (req, res, next) => {
    logger.debug('Permission middleware called', { 
      requiredPermission: permission,
      path: req.path, 
      method: req.method,
      userId: req.user?.userId,
      ip: req.ip
    });
    
    if (!req.user) {
      logger.warn('Permission access attempt without user context', { 
        requiredPermission: permission,
        path: req.path, 
        method: req.method,
        ip: req.ip
      });
      return res.status(401).json({ 
        error: 'Authentication required',
        code: 'NO_USER_CONTEXT',
        message: 'User must be authenticated to access this resource'
      });
    }
    
    const permissions = Array.isArray(req.user.permissions) ? req.user.permissions : [];
    const hasPermission = permissions.includes(permission) || 
                         (req.user.isAdmin === true) || // Admins have all permissions
                         (Array.isArray(req.user.roles) && req.user.roles.includes('admin'));
    
    if (!hasPermission) {
      logger.warn('Permission access denied', { 
        userId: req.user.userId, 
        email: req.user.email,
        requiredPermission: permission,
        userPermissions: permissions,
        isAdmin: req.user.isAdmin,
        path: req.path,
        method: req.method,
        ip: req.ip
      });
      return res.status(403).json({ 
        error: `${permission} permission required`,
        code: 'INSUFFICIENT_PRIVILEGES',
        message: `This action requires ${permission} permission`
      });
    }
    
    logger.debug('Permission access granted', { 
      userId: req.user.userId, 
      email: req.user.email,
      requiredPermission: permission,
      userPermissions: permissions,
      path: req.path
    });
    
    next();
  };
};

module.exports = {
  authenticateToken,
  requireAdmin,
  requireRole,
  requirePermission
};

/**
 * Error Handling Middleware
 * Provides centralized error handling for the application
 */

const logger = require('../utils/logger');
const { ValidationError } = require('../utils/validators');

/**
 * Error handler middleware
 * Must be the last middleware in the chain
 */
const errorHandler = (err, req, res, next) => {
  // Log the error
  logger.error('Unhandled error:', {
    error: err.message,
    stack: err.stack,
    url: req.url,
    method: req.method,
    ip: req.ip || req.connection.remoteAddress,
    userAgent: req.get('User-Agent')
  });

  // Handle validation errors
  if (err instanceof ValidationError) {
    return res.status(400).json({
      error: 'Validation failed',
      details: [{
        field: err.field,
        message: err.message,
        value: err.value
      }]
    });
  }

  // Handle database connection errors
  if (err.code === 'ECONNREFUSED' || err.code === 'ENOTFOUND') {
    return res.status(503).json({
      error: 'Database service unavailable',
      message: 'Please try again later'
    });
  }

  // Handle database constraint violations
  if (err.code === '23505') { // Unique violation
    return res.status(409).json({
      error: 'Duplicate entry',
      message: 'A record with this information already exists'
    });
  }

  if (err.code === '23503') { // Foreign key violation
    return res.status(400).json({
      error: 'Invalid reference',
      message: 'Referenced record does not exist'
    });
  }

  if (err.code === '23514') { // Check violation
    return res.status(400).json({
      error: 'Invalid data',
      message: 'Data does not meet requirements'
    });
  }

  // Handle JWT errors
  if (err.name === 'JsonWebTokenError') {
    return res.status(401).json({
      error: 'Invalid token',
      message: 'Authentication token is invalid'
    });
  }

  if (err.name === 'TokenExpiredError') {
    return res.status(401).json({
      error: 'Token expired',
      message: 'Authentication token has expired'
    });
  }

  // Handle rate limiting errors
  if (err.status === 429) {
    return res.status(429).json({
      error: 'Too many requests',
      message: 'Please try again later'
    });
  }

  // Handle request size errors
  if (err.status === 413) {
    return res.status(413).json({
      error: 'Request too large',
      message: 'Request body exceeds size limit'
    });
  }

  // Handle syntax errors in JSON
  if (err instanceof SyntaxError && err.status === 400 && 'body' in err) {
    return res.status(400).json({
      error: 'Invalid JSON',
      message: 'Request body contains invalid JSON'
    });
  }

  // Handle multer file upload errors
  if (err.code === 'LIMIT_FILE_SIZE') {
    return res.status(413).json({
      error: 'File too large',
      message: 'Uploaded file exceeds size limit'
    });
  }

  if (err.code === 'LIMIT_FILE_COUNT') {
    return res.status(413).json({
      error: 'Too many files',
      message: 'Too many files uploaded'
    });
  }

  if (err.code === 'LIMIT_UNEXPECTED_FILE') {
    return res.status(400).json({
      error: 'Unexpected file field',
      message: 'Unexpected file field in upload'
    });
  }

  // Handle custom upload validation errors
  if (err.statusCode === 415) {
    return res.status(415).json({
      error: 'Unsupported media type',
      message: err.message || 'File type not supported'
    });
  }

  if (err.statusCode === 413) {
    return res.status(413).json({
      error: 'Request entity too large',
      message: err.message || 'Upload exceeds size limits'
    });
  }

  // Handle generic database errors
  if (err.code && err.code.startsWith('23')) {
    return res.status(400).json({
      error: 'Database error',
      message: 'Invalid data provided'
    });
  }

  // Handle generic server errors
  if (err.status) {
    return res.status(err.status).json({
      error: err.message || 'Server error',
      ...(process.env.NODE_ENV === 'development' && { stack: err.stack })
    });
  }

  // Default error response
  const statusCode = err.statusCode || 500;
  const message = err.message || 'Internal server error';

  res.status(statusCode).json({
    error: 'Server error',
    message: process.env.NODE_ENV === 'production' ? 'Something went wrong' : message,
    ...(process.env.NODE_ENV === 'development' && { 
      stack: err.stack,
      details: err
    })
  });
};

/**
 * 404 handler for unmatched routes
 */
const notFoundHandler = (req, res) => {
  logger.warn('Route not found:', {
