function parseSize(size) {
  const units = {
    'b': 1,
    'kb': 1024,
    'mb': 1024 * 1024,
    'gb': 1024 * 1024 * 1024
  };
  
  const match = size.toLowerCase().match(/^(\d+(?:\.\d+)?)\s*(b|kb|mb|gb)$/);
  if (!match) {
    return parseInt(size) || 1024 * 1024; // Default to 1MB
  }
  
  const [, value, unit] = match;
  return parseFloat(value) * units[unit];
}

